#zadanie2#danev0 = 10;g = 9.81;#obliczeniat = ( 2 * v0 ) / g;t_ = [ 0 : 0.25 : t ];h_  = v0 .* t_ - (g .* t_ .^ 2) / 2;v_ = v0 - g .* t_;#wykres h(t)subplot(1, 2, 1);plot(t_, h_);hold on;zera = zeros(1,9);quiver(t_, h_, zera, v_, 0.5);text(t_, h_, strcat(num2str(v_'), ' ', 'm/s'));xlabel('t [s]');ylabel('y [m]');legend({'y(t)', 'v_y(t)'}, 'location', 'southwest');hold off;#wykres v(t)subplot(1, 2, 2);plot(t_, v_);hold on;xlabel('t [s]');ylabel('v_y [m]');hold off;