clear all;#skrypt rysuj젺u roz쓾쯘nie nat傷enia pola elektrycznego dla ujemnego i dodatniego 쓰dunku#dane[X, Y, Z] = sphere(15);[U, V, W] = surfnorm(X, Y, Z);#wykresysubplot(1, 2, 1);quiver3(X, Y, Z, U, V, W, 4);hold on;plot3(0, 0, 0, 'r.', 'markersize', 70);hold off;title('Rozk쓰d nat傷enia pola elektrycznego dla dodatniego 쓰dunku');xlabel('X');ylabel('Y');zlabel('Z');subplot(1, 2, 2);quiver3(X, Y, Z, U*-1, V*-1, W*-1, 4);hold on;plot3(0, 0, 0, 'r.', 'markersize', 70);hold off;title('Rozk쓰d nat傷enia pola elektrycznego dla ujemnego 쓰dunku');xlabel('X');ylabel('Y');zlabel('Z');